INSERT INTO `sys_inbox` VALUES ('10000005730018', '2052751705', '2014-09-17 16:16:38', null, null, '0', '0', '欢迎使用腾讯企业邮箱', '\"=?gb2312?B?zNrRtsbz0rXTys/k08O7pw==?=\"', '10000@qq.com', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\n<head>\n	<title></title>\n<style type=\"text/css\">\nbody{background:#ECECEC;font-family:\"lucida Grande\",Verdana;}\n.invite{width:606px;margin:0 auto;overflow:hidden;}\n.invite, .invite td, .invite th{border-collapse:collapse;padding:0;vertical-align:middle;}\n.invite th{font-weight:bold;font-size:16px;color:white;text-align:left;background:#3B5999;}\n.invite th .mailLogo{margin:-2px 5px 0 0; padding:0 0 0 30px;}\n.invite th div{height:65px;overflow:hidden;}\n.invite .borderLeft{width:4px;*width:3px;border-left:1px solid #EBEBEB;border-right:1px solid #C9C9C9;background:#E9E9E9;}\n.invite .borderRight{width:3px;border-left:1px solid #C9C9C9;border-right:1px solid #EBEBEB;background:#E9E9E9;}\n.invite h2{margin:26px 34px 16px;font-size:18px;font-weight:bold;}\n.invite p{color:#313131;display:block;font-size:14px;line-height:150%;padding:1px 34px 21px;margin:0;}\n.invite p.team, .invite p.team a{color:#999999;text-decoration:none;}\n.invite p.team a:hover{color:#999999;text-decoration:underline;}\n.invite a{color:#3B5999}\n</style>\n</head>\n<body>\n<div style=\"background:#ECECEC;\">\n<table class=\"invite\" align=\"center\" bgcolor=\"#ffffff\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\n	<tr>\n		<td colspan=\"4\" class=\"radius\" style=\"vertical-align:bottom;overflow:hidden;line-height:6px;font-size:0;\"><img src=\"http://exmail.qq.com/zh_CN/htmledition/images/newicon/sysmail/mail_invite_top.png\" alt=\"\" /></td>\n	</tr>\n	<tr>\n		<td rowspan=\"2\" class=\"border borderLeft\"></td>\n		<th width=\"416\"><img src=\"http://exmail.qq.com/zh_CN/htmledition/images/logo/logo_sysmail_biz_0.gif\" alt=\"mail企业邮箱\" class=\"mailLogo\"/>&nbsp;</th>\n		<th width=\"180\"><div><img src=\"http://exmail.qq.com/zh_CN/htmledition/images/bizmail/top_biz.gif\" class=\"mailBg\"/></div></th>\n		<td rowspan=\"2\" class=\"border borderRight\"></td>\n	</tr>\n	<tr>\n		<td colspan=\"2\">\n			<p style=\"margin-top:20px;\">客服，您好：</p>\n			<p>欢迎启用您的企业邮箱！</p>\n			<p>您可以收藏登录地址 <a href=\"http://exmail.qq.com/login\" target=\"_blank\">http://exmail.qq.com/login</a> 快速登录邮箱；或使用客户端管理邮件，查看<a href=\"http://service.exmail.qq.com/cgi-bin/help?subtype=1&&id=28&&no=1000564\" target=\"_blank\">设置方法</a>。</p>\n			<p>腾讯企业邮箱支持发送 50MB 普通附件，2GB 超大附件。我们还准备了：</p>\n			<p>\n			<img src=\"http://exmail.qq.com/zh_CN/htmledition/images/bizmail/icon_bindwx.png\" style=\"vertical-align:middle;margin-right:4px\" height=\"18\" width=\"18px\"/>\n			<strong>微信提醒</strong><br/>\n			<span style=\"margin-left:27px;\">微信助手帮您随时处理新来信、日程提醒，还能快捷查询同事联系方式 。<br/></span>\n			<span style=\"margin-left:27px;\">试试扫描二维码绑定微信？点击 <a href=\"https://exmail.qq.com/cgi-bin/setting_wxbind?action=get\" onclick=\"if(location.href.indexOf(\'file://\')==-1){location.href=\'/cgi-bin/setting_wxbind?action=get&sid=$SID$\';return false;}\">查看二维码</a></span>\n			</p>\n			<p>\n			<img src=\"http://exmail.qq.com/zh_CN/htmledition/images/bizmail/icon_bindqq.png\" style=\"vertical-align:middle;margin-right:4px\" height=\"18\" width=\"18px\"/>\n			<strong>绑定QQ</strong><br/>\n			<span style=\"margin-left:27px;\">想在QQ上接收邮件提醒？希望点击小信封一键登录邮箱？<br/></span>\n			<span style=\"margin-left:27px;\">现在 <a href=\"https://exmail.qq.com/cgi-bin/setting_remind?sid=$SID$&action=list\" onclick=\"if(location.href.indexOf(\'file://\')==-1){location.href=\'/cgi-bin/setting_remind?sid=$SID$&action=list\';return false;}\">绑定QQ</a>，让企业邮箱更方便快捷。</span>\n			</p>\n			<p>\n				<img src=\"http://exmail.qq.com/zh_CN/htmledition/images/bizmail/icon_exsfile.gif\" style=\"vertical-align:middle;margin-right:4px\" height=\"18\" width=\"18px\"/>\n				<strong>企业网盘</strong><br/>\n				<span style=\"margin-left:27px;\">与同事共享文档、照片变得更简单，只需上传到企业网盘轻松共享。</span>\n			</p>\n			\n			<p>\n				<img src=\"http://service.mail.qq.com/images/faq/foxmail_icon_16_blue.png\" style=\"vertical-align:middle;margin-right:4px\" height=\"18\" width=\"18px\"/>\n				<strong>专属客户端Foxmail</strong><br/>\n				<span style=\"margin-left:27px;\">Foxmail，帮你自动配置腾讯企业邮箱帐号，实时同步地址本、日历。<br/></span>\n				<span style=\"margin-left:27px;\">支持发送高达2G的超大附件，各类附件轻松送达。<a href=\"http://exmail.qq.com/cgi-bin/readtemplate?t=bizmail_foxmail_introduce\" target=\"_blank\">了解更多</a></span>\n			\n			<p style=\"margin-left:27px;\">\n				您还可了解腾讯企业邮箱<a href=\"http://exmail.qq.com/portal/product/function\" target=\"_blank\">更多实用功能</a>。\n			</p>\n			<p>\n				祝您使用愉快。如果有任何疑惑，欢迎与我们联系： <a href=\"mailto:bizsupport@qq.com\">bizsupport@qq.com</a> 。\n			</p>\n			<p class=\"team\">\n				腾讯企业邮箱<br />\n				<span style=\"background:#ddd;height:1px;width:100%;overflow:hidden;display:block;margin:2px 0;\"></span>\n				腾讯企业邮箱门户：<a href=\"http://exmail.qq.com\" target=\"_blank\">http://exmail.qq.com</a><br/>\n				企业邮箱帮助中心：<a href=\"http://service.exmail.qq.com\" target=\"_blank\">http://service.exmail.qq.com</a>	\n			</p>\n		</td>\n	</tr>\n	<tr>\n		<td colspan=\"4\"  style=\"vertical-align:top;line-height:5px;overflow:hidden;font-size:0;\"><img src=\"http://exmail.qq.com/zh_CN/htmledition/images/newicon/sysmail/mail_invite_bottom.png\" alt=\"\" /></td>\n	</tr>\n</table>\n</div>\n</body>\n</html>\n\r\n', '2014-08-29 18:49:54', '', '2052751705', '0', null, 'support@peisheyun.com');
