INSERT INTO `sys_desktopitem` VALUES ('10000003170014', '2052751705', '2013-10-11 16:35:52', '2052751705', '2014-09-17 16:31:55', '0', '0', '组件统计', '10000002970000', '', '/pages/resource/compexlist.action?listId=144', '<script type=\"text/javascript\">\r\nfunction loadChart(height) {\r\n	$.ajax( {\r\n		type : \'POST\',\r\n		url : __basePath+\'/pages/resource/useinfo/state.action?number=9\',\r\n		dataType:\'json\',\r\n		async:false,\r\n                global:false,\r\n		success : function(data) {\r\n			if($.browser.version < 9){\r\n				buildFSPie(data,height);\r\n			}else{\r\n				try{\r\n					buildPie(data,height);	\r\n				}catch(e){}\r\n			}\r\n		}\r\n	});\r\n}\r\n\r\n//生成饼图\r\nfunction buildPie(data,height) {\r\n	var chart = new iChart.Pie3D({\r\n		render : \'chartId\',\r\n		data: data,\r\n		sub_option : {\r\n			mini_label_threshold_angle : 40,//迷你label的阀值,单位:角度\r\n			mini_label:{//迷你label配置项\r\n				fontsize:20,\r\n				fontweight:600,\r\n				color : \'#ffffff\'\r\n			},\r\n			label : {\r\n				background_color:null,\r\n				sign:false,//设置禁用label的小图标\r\n				padding:\'0 6\',\r\n				border:{\r\n					enable:false,\r\n					color:\'#666666\'\r\n				},\r\n				fontsize:11,\r\n				fontweight:600,\r\n				color : \'#4572a7\'\r\n			},\r\n			border : {\r\n				width : 2,\r\n				color : \'#ffffff\'\r\n			},\r\n			listeners:{\r\n				parseText:function(d, t){\r\n					return d.get(\'value\');//自定义label文本\r\n				}\r\n			} \r\n		},\r\n		legend:{\r\n			enable:true,\r\n			padding:0,\r\n			line_height:20,//设置行高\r\n			sign_space:10,//小图标与文本间距\r\n			border:false,\r\n			align:\'right\',\r\n			background_color : null//透明背景\r\n		}, \r\n		shadow : true,\r\n		shadow_blur : 6,\r\n		shadow_color : \'#aaaaaa\',\r\n		shadow_offsetx : 0,\r\n		shadow_offsety : 0,\r\n		background_color:\'#f8f8f8\',\r\n		border : {\r\n			color : \'#f8f8f8\'\r\n		},\r\n		align:\'left\',//右对齐\r\n		offsetx:60,//设置向x轴负方向偏移位置60px\r\n		offset_angle:-90,//逆时针偏移120度\r\n		width : $(\"#chartId\").parent().width(),\r\n		height : height,\r\n		radius:150\r\n	});\r\n	chart.bound(0);\r\n}\r\n\r\nfunction buildFSPie(data,height) {\r\n	var tmp = systemSort(data);\r\n	var dataXml = \"<chart formatNumberScale=\'0\' baseFontSize=\'12\' palette=\'4\' decimals=\'0\' enableSmartLabels=\'1\' enableRotation=\'0\' bgColor=\'99CCFF,FFFFFF\' bgAlpha=\'40,100\' bgRatio=\'0,100\' bgAngle=\'360\' showBorder=\'1\' startingAngle=\'70\' >\";\r\n   	for(var i=0; i<tmp.length; i++) {\r\n   		if(i==0) {\r\n   			dataXml += \"<set label=\'\" + data[i][\"name\"] + \"\' value=\'\" + data[i][\"value\"] + \"\' isSliced=\'1\'></set>\";\r\n   		}else{\r\n			dataXml += \"<set label=\'\" + data[i][\"name\"] + \"\' value=\'\" + data[i][\"value\"] + \"\'></set>\";    			\r\n   		}\r\n   	}\r\n   	dataXml += \"</chart>\";    	\r\n   	var width = Math.floor($(\"#chartId\").parent().width());\r\n   	height = Math.floor(height);\r\n   	var myChart = new FusionCharts(\"<c:url value=\'/js/FusionCharts/Charts/Pie3D.swf\'></c:url>\", \"cId\", width, height, \"0\", \"0\");\r\n   	myChart.setDataXML(dataXml);\r\n   	myChart.setTransparent(true);\r\n	myChart.render(\"chartId\");\r\n	document.title = kingtamTilte;\r\n}\r\n\r\n$(function(){\r\n  loadChart(271);\r\n})\r\n</script>\r\n\r\n<div id=\"chartId\" align=\"center\" ></div>\r\n\r\n');
